mapper.select:
select im.id, im.code, (case when imt.name is not null then imt.name else im.name end ) name, im.fd_level, im.parent_id, im.type, im.sort, im.icon, im.h_quick_index, (case when im.route is null then '' else im.route end) route from iam_menu im
INNER JOIN hiam_label_rel hlr ON hlr.data_type = #{menuLabelDataType} AND hlr.data_id = im.id INNER JOIN iam_label il ON hlr.label_id = il.id AND il.name IN
#{label}
left join iam_menu_tl imt on imt.id = im.id and imt.lang = #{lang} where im.type in ('root', 'dir', 'menu', 'link', 'inner-link', 'window') and im.h_enabled_flag = 1 and im.h_virtual_flag = 0 and (im.h_tenant_id = 0 or im.h_tenant_id = #{tenantId})
AND (SELECT COUNT(1) FROM hiam_label_rel hlr INNER JOIN iam_label il ON hlr.label_id = il.id WHERE hlr.data_type = #{menuLabelDataType} AND hlr.data_id = im.id AND il.name IN
#{label}
) = #{labelsSize}

select DISTINCT im.id, im.code, (case when imt.name is not null then imt.name else im.name end ) name, im.fd_level, im.parent_id, im.type, im.sort, im.icon, im.h_quick_index, (case when im.route is null then '' else im.route end) route from iam_menu im
INNER JOIN hiam_label_rel hlr ON hlr.data_type = #{menuLabelDataType} AND hlr.data_id = im.id INNER JOIN iam_label il ON hlr.label_id = il.id AND il.name IN
#{label}
left join iam_menu_tl imt on imt.id = im.id and imt.lang = #{lang} where im.type in ('root', 'dir', 'menu', 'link', 'inner-link', 'window') and im.h_enabled_flag = 1 and im.h_virtual_flag = 0
AND (SELECT COUNT(1) FROM hiam_label_rel hlr INNER JOIN iam_label il ON hlr.label_id = il.id WHERE hlr.data_type = #{menuLabelDataType} AND hlr.data_id = im.id AND il.name IN
#{label}
) = #{labelsSize}

SELECT im.id, im.code, im.name, im.parent_id, im.type, im.sort, im.icon, im.h_permission_type, im.h_controller_type, im.h_level_path, im.fd_level FROM iam_menu im LEFT JOIN hiam_label_rel hlr ON hlr.data_id = im.id LEFT JOIN iam_label il ON il.id = hlr.label_id
and im.type IN
#{type}
and il.name IN
#{label}
group by im.id ORDER BY im.sort

select DISTINCT ps.code, ps.h_controller_type controller_type, 1 approve from iam_menu ps join iam_role ir on ps.fd_level = ir.fd_level and ir.id in
#{roleId}
JOIN iam_member_role imr ON imr.role_id = ir.id AND imr.member_id = #{userId} JOIN fd_project_permission fpu ON fpu.MEMBER_ROLE_ID = imr.id AND fpu.PROJECT_ID = #{projectId} JOIN iam_menu im ON im.id = ps.parent_id JOIN hiam_label_rel hlr1 ON hlr1.data_type = 'MENU' AND hlr1.data_id = im.id JOIN iam_label il1 ON il1.id = hlr1.label_id AND il1.type = 'MENU' and il1.name in
#{label}
join iam_role_permission irp on ( irp.role_id = ir.id and ps.id = irp.permission_id and irp.type = 'PS' and (irp.h_create_flag = 'Y' OR irp.h_inherit_flag = 'Y') )
or ps.code like concat('%',#{code})

SELECT im.* FROM iam_role_permission irp JOIN iam_menu im ON im.id = irp.permission_id AND irp.type = 'PS' WHERE im.fd_level = #{level} and irp.role_id in
#{roleId}
and (irp.h_create_flag = 'Y' OR irp.h_inherit_flag = 'Y')

SELECT count(*) FROM iam_role_permission irp JOIN iam_menu im ON im.id = irp.permission_id AND irp.type = 'PS' WHERE im.fd_level = #{level} and irp.role_id in
#{roleId}
and (irp.h_create_flag = 'Y' OR irp.h_inherit_flag = 'Y')
