SELECT DISTINCT
fp.*
FROM
fd_project fp
INNER JOIN hzero_platform.iam_member_role imr ON imr.source_id = fp.ORGANIZATION_ID
INNER JOIN hzero_platform.fd_project_permission pu ON pu.MEMBER_ROLE_ID = imr.id
WHERE imr.member_id = #{userId}
AND fp.organization_id = #{orgId}
AND fp.id = pu.PROJECT_ID

    AND is_enabled = #{isEnabled}

select * from
fd_project fp
left join fd_project_map_category fpmc ON fpmc.PROJECT_ID = fp.id
left join fd_project_category fpc ON fpc.id =fpmc.CATEGORY_ID
WHERE fp.organization_id=#{organizationId}
AND fpc.CODE = #{agile}
AND fp.is_enabled=1

    AND (
    fp.name like concat(concat('%',#{param}),'%')
    )

SELECT fp.*, iu.real_name as create_user_name, iu.image_url as create_user_image_url, null as program_name FROM fd_project fp left join iam_user iu on iu.id = fp.created_by
INNER JOIN fd_project_permission fpu ON fpu.PROJECT_ID = fp.id INNER JOIN iam_member_role imr ON imr.id = fpu.MEMBER_ROLE_ID INNER JOIN iam_role ir ON (ir.is_enabled = 1 and ir.id = imr.role_id)
LEFT JOIN fd_project_permission fpu ON fpu.PROJECT_ID = fp.id LEFT JOIN iam_member_role imr ON imr.id = fpu.MEMBER_ROLE_ID LEFT JOIN iam_role ir ON (ir.is_enabled = 1 and ir.id = imr.role_id)
fp.organization_id = #{organizationId}
AND imr.member_id = #{userId} AND imr.member_type = 'user' AND imr.source_type = 'organization' AND imr.source_id = #{organizationId}
AND fp.name LIKE concat(concat('%',#{projectDTO.name}),'%')
AND fp.code LIKE concat(concat('%',#{projectDTO.code}),'%')
AND fp.category LIKE concat(concat('%',#{projectDTO.category}),'%')
AND fp.is_enabled = #{projectDTO.enabled}
AND fp.created_by = #{userId}
AND ( fp.name LIKE concat(concat('%', #{params}), '%') OR fp.code LIKE concat(concat('%', #{params}), '%') )
GROUP BY fp.id order by fp.is_enabled desc,fp.creation_date desc

select distinct fp.*
from fd_project fp
INNER JOIN fd_project_permission fpu ON (fp.id = fpu.PROJECT_ID)
INNER JOIN iam_member_role imr ON (imr.id = fpu.MEMBER_ROLE_ID)

    imr.member_id = #{userId}
    AND imr.member_type = 'user'


            AND fp.organization_id = #{projectDTO.organizationId}


            AND fp.name LIKE concat(concat('%',#{projectDTO.name}),'%')


            AND fp.is_enabled = #{projectDTO.enabled}

select fp.*
from fd_project fp
where fp.organization_id = #{organizationId}

    AND fp.name LIKE concat(concat('%',#{name}),'%')

order by fp.creation_date desc

    limit #{limit}

SELECT fp.*, iu.real_name as create_user_name, iu.image_url as create_user_image_url, null as program_name, ir.id AS role_id, ir.name AS role_name, ir.code AS role_code, ir.is_enabled AS role_is_enabled, ir.h_tenant_id as role_org_id, ir.object_version_number as role_version_number, ir.created_by as role_created_by, ir.creation_date as role_creation_date, fpc.id as category_id, fpc.name as category_name, fpc.code as category_code, fpc.object_version_number as category_version_number, fpc.organization_id as category_org_id, fpc.created_by as category_created_by, fpc.creation_date as category_creation_date FROM fd_project fp inner join fd_project_map_category fpmc on fp.id = fpmc.project_id inner join fd_project_category fpc on fpc.id = fpmc.category_id left join iam_user iu on iu.id = fp.created_by
INNER JOIN fd_project_permission fpu ON fpu.PROJECT_ID = fp.id INNER JOIN iam_member_role imr ON imr.id = fpu.MEMBER_ROLE_ID INNER JOIN iam_role ir ON (ir.is_enabled = 1 and ir.id = imr.role_id)
LEFT JOIN fd_project_permission fpu ON fpu.PROJECT_ID = fp.id LEFT JOIN iam_member_role imr ON imr.id = fpu.MEMBER_ROLE_ID LEFT JOIN iam_role ir ON (ir.is_enabled = 1 and ir.id = imr.role_id)
fp.organization_id = #{organizationId}
AND imr.member_id = #{userId} AND imr.member_type = 'user' AND imr.source_type = 'organization' AND imr.source_id = #{organizationId}
AND fp.name LIKE concat(concat('%',#{projectDTO.name}),'%')
AND fp.code LIKE concat(concat('%',#{projectDTO.code}),'%')
AND fp.category LIKE concat(concat('%',#{projectDTO.category}),'%')
AND fp.is_enabled = #{projectDTO.enabled}
AND fp.created_by = #{userId}
AND ( fp.name LIKE concat(concat('%', #{params}), '%') OR fp.code LIKE concat(concat('%', #{params}), '%') )
order by fp.is_enabled desc,fp.creation_date desc, fpmc.id asc

select fp.*, fpc.id as category_id, fpc.name as category_name, fpc.code as category_code, fpc.object_version_number as category_version_number, fpc.organization_id as category_org_id, iu.real_name as create_user_name, iu.image_url as create_user_image_url from fd_project fp left join fd_project_map_category fpmc on fp.id = fpmc.project_id left join fd_project_category fpc on fpc.id = fpmc.category_id left join iam_user iu on fp.CREATED_BY=iu.id Where fp.id IN
#{projectId}
order by fpmc.id asc

select fp.*, fpc.id as category_id, fpc.name as category_name, fpc.code as category_code, fpc.object_version_number as category_version_number, fpc.organization_id as category_org_id FROM fd_project fp JOIN fd_project_map_category fpmc ON fp.ID = fpmc.PROJECT_ID JOIN fd_project_category fpc ON fpc.ID = fpmc.CATEGORY_ID WHERE fp.ORGANIZATION_ID = #{organizationId}
AND fpc.id IN
#{id}
AND fpc.code IN
#{code}
AND fp.is_enabled = #{projectSearchVO.enable}
