select count(1) from ( select distinct iut.id from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut left join iam_member_role imr on iut.id = imr.member_id left join fd_project_permission fdu on (imr.id = fdu.MEMBER_ROLE_ID) JOIN iam_role ir on ir.id = imr.role_id
left join iam_role ir2 on ir2.id = imr.role_id
fdu.PROJECT_ID = #{sourceId}
and ir.name like concat(concat('%',#{roleName}),'%')
and iut.real_name LIKE concat(concat('%',#{realName}),'%')
and iut.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and iut.is_enabled = #{enabled}
and ( iut.target_login_name like concat(concat('%',#{params}),'%') or iut.real_name like concat(concat('%',#{params}),'%') OR iut.attribute15 LIKE concat(concat('%',#{params}),'%') OR iut.attribute14 LIKE concat(concat('%',#{params}),'%') )
) t

select t2.id,t2.real_name,t2.target_login_name as login_name,t2.is_enabled,t2.is_ldap, t2.is_locked,t2.organization_id,t2.email,t2.language,t2.time_zone, t2.phone,t2.image_url, t2.object_version_number as user_version_number, fo.tenant_name as user_org_name, htc.config_value as user_org_code, ir2.id as role_id,ir2.code as role_code, ir2.name as role_name, ir2.is_enabled as role_is_enabled,ir2.h_tenant_id as role_org_id from ( select distinct t1.* from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut join iam_member_role imr ON iut.id = imr.member_id join fd_project_permission fdu on fdu.MEMBER_ROLE_ID = imr.id JOIN iam_role ir2 on ir2.id=imr.role_id
left join iam_role ir on ir.id = imr.role_id
fdu.PROJECT_ID = #{sourceId}
and ir.name like concat(concat('%',#{roleName}),'%')
and iut.real_name LIKE concat(concat('%',#{realName}),'%')
and iut.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and iut.is_enabled = #{enabled}
and ( iut.target_login_name like concat(concat('%',#{params}),'%') or iut.real_name like concat(concat('%',#{params}),'%') OR iut.attribute15 LIKE concat(concat('%',#{params}),'%') OR iut.attribute14 LIKE concat(concat('%',#{params}),'%') )
) t1 order by t1.is_enabled desc, t1.id desc
limit #{start}, #{size}
) t2 join iam_member_role imr2 ON t2.id = imr2.member_id join fd_project_permission fdu2 on imr2.id = fdu2.MEMBER_ROLE_ID and fdu2.PROJECT_ID = #{sourceId} left join iam_role ir2 on ir2.id = imr2.role_id left join hpfm_tenant fo on (t2.organization_id = fo.tenant_id) left join hpfm_tenant_config htc ON fo.tenant_id = htc.tenant_id and htc.config_key = 'code'

select t2.id,t2.real_name,t2.target_login_name as login_name,t2.is_enabled, t2.image_url, t2.is_locked,t2.organization_id,t2.email,t2.language,t2.time_zone, t2.phone,t2.is_ldap, t2.object_version_number as user_version_number, fo.tenant_name as user_org_name,htc.config_value as user_org_code, ir2.id as role_id,ir2.code as role_code, ir2.name as role_name, ir2.is_enabled as role_is_enabled,ir2.h_tenant_id as role_org_id from ( select distinct t1.* from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu
WHERE iu.ID IN
#{userId,jdbcType=BIGINT}
) iut left join iam_member_role imr ON iut.id = imr.member_id left join fd_project_permission fdu on imr.id = fdu.MEMBER_ROLE_ID JOIN iam_role ir2 on ir2.id=imr.role_id
fdu.PROJECT_ID = #{projectId,jdbcType=BIGINT}
) t1 ) t2 left join
(select imr2.role_id, imr2.member_id from iam_member_role imr2 inner join fd_project_permission fpu2 on (imr2.id = fpu2.MEMBER_ROLE_ID and fpu2.PROJECT_ID = #{projectId,jdbcType=BIGINT}) ) mr ON t2.id = mr.member_id
left join iam_role ir2 on ir2.id = mr.role_id left join hpfm_tenant fo on (t2.organization_id = fo.tenant_id) left join hpfm_tenant_config htc ON fo.tenant_id = htc.tenant_id and htc.config_key = 'code'

SELECT iu.* FROM fd_project_permission fpu INNER JOIN iam_member_role imr ON imr.id = fpu.MEMBER_ROLE_ID INNER JOIN iam_user iu ON iu.id = imr.member_id WHERE fpu.PROJECT_ID = #{projectId}
AND (iu.login_name LIKE concat(concat('%', #{param}),'%') OR iu.real_name LIKE concat(concat('%',#{param}),'%') OR iu.attribute15 LIKE concat(concat('%',#{param}),'%') OR iu.attribute14 LIKE concat(concat('%',#{param}),'%') )
GROUP BY iu.id limit 20

SELECT imr.member_id as id, GROUP_CONCAT(ir.name) as role_names,ANY_VALUE(iu.login_name) as login_name,ANY_VALUE(iu.real_name) as real_name,ANY_VALUE(iu.image_url) as image_url FROM fd_project_permission fpu INNER JOIN iam_member_role imr ON imr.id = fpu.MEMBER_ROLE_ID INNER JOIN iam_role ir ON imr.role_id = ir.id INNER JOIN iam_user iu ON iu.id=imr.member_id WHERE fpu.PROJECT_ID = #{projectId,jdbcType=BIGINT} and imr.member_type='user' AND imr.MEMBER_ID IN
#{userId}
GROUP BY imr.member_id

SELECT iu.id, iu.login_name, iu.email, iu.organization_id, iu.real_name, iu.phone, iu.INTERNATIONAL_TEL_CODE, iu.image_url, iu.profile_photo, iu.time_zone, iu.last_password_updated_at, iu.last_login_at, iu.is_enabled AS enabled, iu.is_locked AS locked, iu.is_ldap AS ldap, iu.is_admin AS admin, iu.locked_until_at, iu.password_attempt, iu.object_version_number, iu.created_by, iu.creation_date, iu.last_updated_by, iu.last_update_date, iu.user_type FROM iam_user iu INNER JOIN iam_member_role imr ON iu.id = imr.member_id LEFT JOIN fd_project_permission fpu ON imr.id = fpu.MEMBER_ROLE_ID
fpu.PROJECT_ID = #{projectId}
AND iu.id = #{userId}
AND iu.email LIKE concat(concat('%',#{email}),'%')
AND ( iu.login_name LIKE concat(concat('%',#{param}),'%') OR iu.real_name LIKE concat(concat('%',#{param}),'%') OR iu.attribute15 LIKE concat(concat('%',#{param}),'%') OR iu.attribute14 LIKE concat(concat('%',#{param}),'%') )
group by iu.id

select iu1.id, iu1.login_name, iu1.email, iu1.organization_id, iu1.real_name, iu1.phone, iu1.INTERNATIONAL_TEL_CODE, iu1.image_url, iu1.profile_photo, iu1.time_zone, iu1.last_password_updated_at, iu1.last_login_at, iu1.is_enabled AS enabled, iu1.is_locked AS locked, iu1.is_ldap AS ldap, iu1.is_admin AS admin, iu1.locked_until_at, iu1.password_attempt, iu1.object_version_number, iu1.created_by, iu1.creation_date, iu1.last_updated_by, iu1.last_update_date, iu1.user_type, t1.weight from ( select t.member_id, max(t.weight) as weight from ( select imr.member_id, case when imr.role_id = #{projectAdminId} then 30 when imr.role_id = #{projectMemberId} then 20 else 10 end as weight from iam_member_role imr join fd_project_permission fpu on imr.id = fpu.member_role_id left join iam_user iu on imr.member_id = iu.id where fpu.project_id = #{projectId}
AND iu.id = #{userId}
AND iu.email LIKE concat(concat('%',#{email}),'%')
AND iu.id NOT IN
#{id,jdbcType=BIGINT}
AND ( iu.login_name LIKE concat(concat('%',#{param}),'%') OR iu.real_name LIKE concat(concat('%',#{param}),'%') OR iu.attribute15 LIKE concat(concat('%',#{param}),'%') OR iu.attribute14 LIKE concat(concat('%',#{param}),'%') )
) t group by t.member_id ) t1 join iam_user iu1 on t1.member_id = iu1.id order by t1.weight desc, iu1.id asc

SELECT iu.* FROM fd_project_permission fpu INNER join iam_member_role imr ON imr.id = fpu.MEMBER_ROLE_ID INNER JOIN iam_user iu ON imr.member_id = iu.id
fpu.PROJECT_ID = #{projectId}
AND imr.ROLE_ID = #{roleId}
AND iu.login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')
AND iu.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')
AND iu.is_enabled = #{roleAssignmentSearchDTO.enabled}
AND imr.role_id IN
#{item}
AND ( iu.login_name LIKE concat(concat('%',#{param}),'%') OR iu.real_name LIKE concat(concat('%',#{param}),'%') OR iu.attribute15 LIKE concat(concat('%',#{param}),'%') OR iu.attribute14 LIKE concat(concat('%',#{param}),'%') )

select iu1.*, case when iu1.is_enabled = 0
then t1.weight - 100 else t1.weight end as weight from ( select t.member_id, max(t.weight) as weight from ( select imr.member_id, case when imr.role_id in
#{adminRole}
then 10 else 0 end as weight from iam_member_role imr join fd_project_permission fpu on imr.id = fpu.member_role_id where fpu.project_id = #{projectId}
union select iu.id, 0 as weight from iam_user iu where iu.id in
#{id}
) t group by t.member_id ) t1 join iam_user iu1 on t1.member_id = iu1.id where 1=1
AND ( iu1.login_name LIKE concat(concat('%',#{param}),'%') OR iu1.real_name LIKE concat(concat('%',#{param}),'%') )
order by weight desc, iu1.id asc

select iu1.*, case when iu1.is_enabled = 0
then t1.weight - 100 else t1.weight end as weight from ( select t.member_id, max(t.weight) as weight from ( select imr.member_id, case when imr.role_id in
#{adminRole}
then 10 else 0 end as weight from iam_member_role imr join fd_project_permission fpu on imr.id = fpu.member_role_id where fpu.project_id in
#{projectId}
union select iu.id, 0 as weight from iam_user iu where iu.id in
#{id}
) t group by t.member_id ) t1 join iam_user iu1 on t1.member_id = iu1.id where 1=1
AND ( iu1.login_name LIKE concat(concat('%',#{param}),'%') OR iu1.real_name LIKE concat(concat('%',#{param}),'%') OR iu1.email LIKE concat(concat('%',#{param}),'%') OR iu1.attribute15 LIKE concat(concat('%',#{param}),'%') OR iu1.attribute14 LIKE concat(concat('%',#{param}),'%') )
