mapper.select:
SELECT role.*
FROM
iam_role role
LEFT JOIN iam_member_role imr ON role.id = imr.role_id
WHERE
imr.member_id = #{userId}
AND imr.member_type = 'user'
AND imr.source_id = #{sourceId}
AND imr.source_type = #{sourceType}


SELECT ir.*
FROM iam_role ir
LEFT JOIN hiam_label_rel hlr ON (ir.id = hlr.data_id and hlr.data_type = #{roleLabelDataType})
LEFT JOIN iam_label il ON hlr.label_id = il.id
WHERE il.name = #{name}
AND il.type = #{type}


        AND ir.h_tenant_id = #{organizationId}


        AND ir.h_tenant_id is null

SELECT ir.* FROM iam_role ir inner JOIN hiam_label_rel hlr on hlr.data_id=ir.id INNER JOIN iam_label il on il.id=hlr.label_id and il.name=#{labelName} WHERE ir.fd_level = #{sourceType}
AND ir.h_tenant_id =#{sourceId}
AND ir.NAME LIKE CONCAT('%', CONCAT(#{roleName}, '%'))
AND ir.is_enabled = 1

SELECT ir.* FROM iam_role ir inner JOIN hiam_label_rel hlr on hlr.data_id=ir.id INNER JOIN iam_label il on il.id=hlr.label_id
ir.fd_level = #{level} AND ir.h_tenant_id =#{tenantId}
and ir.name like concat(concat('%',#{name}), '%')
and ir.code like concat(concat('%',#{code}), '%')
and ir.is_enabled = #{enabled}
and ir.is_built_in = #{builtIn}
and il.name=#{labelName}
and il.name in ('TENANT_ROLE','PROJECT_ROLE')
and ( ir.name like concat(concat('%',#{params}), '%') OR ir.code like concat(concat('%',#{params}), '%') )
group by ir.id order by ir.is_modified ASC, ir.is_enabled DESC, ir.id DESC

SELECT ir.* FROM iam_role ir INNER JOIN hiam_label_rel hlr ON hlr.data_id = ir.id INNER JOIN iam_label il ON il.id = hlr.label_id
AND il. NAME = #{labelName}
AND il. NAME = 'TENANT_ROLE'
WHERE ir.fd_level = 'organization' AND ir.h_tenant_id = #{tenantId}
and ir.name like concat(concat('%',#{name}), '%')
AND ir.code like concat(concat('%',#{code}), '%')
and ir.is_enabled = #{enabled}

SELECT imr.source_id FROM iam_member_role imr inner join iam_role ir on ir.id = imr.role_id INNER JOIN iam_role_permission irp ON imr.role_id = irp.role_id INNER JOIN iam_menu im ON im.id = irp.permission_id INNER JOIN hiam_label_rel hlr ON (hlr.data_type = 'MENU' AND hlr.data_id = im.id) INNER JOIN iam_label il ON il.id = hlr.label_id WHERE ir.is_enabled = 1 and il.name = 'TENANT_MENU' AND imr.member_type = 'user' AND imr.member_id = #{userId} AND imr.source_type = 'organization' AND imr.source_id IN
#{orgId}
GROUP BY imr.source_id

SELECT *
FROM iam_role ir
WHERE ir.h_tenant_id = #{tenantId}

SELECT ir.*
FROM iam_role ir
WHERE ir.h_inherit_role_id = #{roleId}

SELECT ir.id, ir.`name`, ir.`code` FROM iam_role ir WHERE ir.id IN
#{roleId}
AND ir.h_tenant_id = #{tenantId} AND ir.fd_level = 'organization'
