mapper.select:
SELECT *,is_ldap as ldap FROM iam_user WHERE id IN
#{id}
AND is_enabled = 1

SELECT * FROM iam_user WHERE email IN
#{email}
AND is_enabled = 1

SELECT * FROM iam_user WHERE login_name IN
#{loginName}
AND is_enabled = 1

SELECT COUNT(*)
FROM iam_user
WHERE creation_date BETWEEN #{begin}
          AND #{end}

select count(1) from iam_user iu where iu.is_enabled = true
AND iu.organization_id = #{organizationId}
AND iu.creation_date < CONCAT('','${startTime}',' 00:00:00')

select t2.is_ldap,t2.id,t2.real_name,t2.target_login_name as login_name,t2.is_enabled, t2.is_locked,t2.organization_id,t2.email,t2.language,t2.time_zone, t2.phone, t2.object_version_number as user_version_number, fo2.name as user_org_name,fo2.code as user_org_code, ir2.id as role_id,ir2.code as role_code, ir2.name as role_name, ir2.is_enabled as role_is_enabled,ir2.organization_id as role_org_id from ( select distinct t1.* from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut
left join hpfm_tenant fo on fo.id = iut.organization_id
left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') left join iam_role ir on ir.id = imr.role_id
and fo.name like concat(concat('%',#{orgName}),'%')
and imr.source_id = #{sourceId} and imr.source_type = #{sourceType} and ir.name like concat(concat('%',#{roleName}),'%')
and iut.real_name LIKE concat(concat('%',#{realName}),'%')
and iut.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and iut.is_enabled = #{enabled}
and iut.is_locked = #{locked}
and ( iut.target_login_name like concat(concat('%',#{params}),'%') or iut.real_name like concat(concat('%',#{params}),'%') )
) t1 order by t1.is_enabled desc, t1.id desc
limit #{start}, #{size}
) t2 left join iam_member_role imr2 on (imr2.member_id = t2.id and imr2.member_type = 'user' and imr2.source_id = #{sourceId} and imr2.source_type = #{sourceType}) left join iam_role ir2 on ir2.id = imr2.role_id left join hpfm_tenant fo2 on (t2.organization_id = fo2.id)

SELECT COUNT(1) FROM (
SELECT DISTINCT iu.id FROM iam_member_role imr
INNER JOIN iam_user iu ON imr.member_id = iu.id
WHERE imr.role_id = #{roleId}
AND imr.member_type = #{memberType}

    AND imr.source_id = #{sourceId}


    AND imr.source_type = #{sourceType}



        AND iu.login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')


        AND iu.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')



    AND (
    iu.login_name LIKE concat(concat('%',#{param}),'%') OR
    iu.real_name LIKE concat(concat('%',#{param}),'%')
    )

) t

select count(1) from ( select distinct t1.id from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') left join iam_role ir on ir.id = imr.role_id where imr.source_id = #{sourceId} and imr.source_type = #{sourceType}
and ir.name like concat(concat('%',#{roleName}),'%')
union select iu2.*, case when iu2.is_ldap=1 then iu2.login_name else iu2.email end as target_login_name from iam_user iu2 where iu2.organization_id = #{sourceId}
) t1
and t1.real_name LIKE concat(concat('%',#{realName}),'%')
and t1.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and t1.is_enabled = #{enabled}
and t1.is_locked = #{locked}
and ( t1.target_login_name like concat(concat('%',#{params}),'%') or t1.real_name like concat(concat('%',#{params}),'%') )
) t

select t2.is_ldap,t2.id,t2.real_name,t2.target_login_name as login_name,t2.is_enabled, t2.is_locked,t2.organization_id, t2.email,t2.language,t2.time_zone, t2.phone, t2.object_version_number as user_version_number, ir2.id as role_id,ir2.code as role_code, ir2.name as role_name,ir2.is_enabled as role_is_enabled,ir2.h_tenant_id as role_org_id from ( select distinct t1.* from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') left join iam_role ir on ir.id = imr.role_id left join hiam_label_rel hlr on (hlr.data_id = ir.id and hlr.data_type = 'ROLE') LEFT JOIN iam_label il on il.id = hlr.label_id where imr.source_id = #{sourceId} and imr.source_type = #{sourceType} and il.name = 'TENANT_ROLE'
and ir.name like concat(concat('%',#{roleName}),'%')
union select iu2.*, case when iu2.is_ldap=1 then iu2.login_name else iu2.email end as target_login_name from iam_user iu2 where iu2.organization_id = #{sourceId}
) t1
and t1.real_name LIKE concat(concat('%',#{realName}),'%')
and t1.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and t1.is_enabled = #{enabled}
and t1.is_locked = #{locked}
and ( t1.target_login_name like concat(concat('%',#{params}),'%') or t1.real_name like concat(concat('%',#{params}),'%') )
order by t1.is_enabled desc, t1.id desc
limit #{start}, #{size}
) t2 left join iam_member_role imr2 on (imr2.member_id = t2.id and imr2.member_type = 'user' and imr2.source_id = #{sourceId} and imr2.source_type = #{sourceType}) left join iam_role ir2 on ir2.id = imr2.role_id

select count(1) from ( select distinct iut.id from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') JOIN iam_role ir on ir.id=imr.role_id
left join iam_role ir2 on ir2.id = imr.role_id
imr.source_id = #{sourceId} and imr.source_type = #{sourceType}
and ir.name like concat(concat('%',#{roleName}),'%')
and iut.real_name LIKE concat(concat('%',#{realName}),'%')
and iut.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and iut.is_enabled = #{enabled}
and ( iut.target_login_name like concat(concat('%',#{params}),'%') or iut.real_name like concat(concat('%',#{params}),'%') )
) t

select t2.id,t2.real_name,t2.target_login_name as login_name,t2.is_enabled,t2.is_ldap, t2.is_locked,t2.organization_id,t2.email,t2.language,t2.time_zone, t2.phone,t2.image_url, t2.object_version_number as user_version_number, fo.name as user_org_name,fo.code as user_org_code, ir2.id as role_id,ir2.code as role_code, ir2.name as role_name, ir2.is_enabled as role_is_enabled,ir2.organization_id as role_org_id from ( select distinct t1.* from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') JOIN iam_role ir2 on ir2.id=imr.role_id
left join iam_role ir on ir.id = imr.role_id
imr.source_id = #{sourceId} and imr.source_type = #{sourceType}
and ir.name like concat(concat('%',#{roleName}),'%')
and iut.real_name LIKE concat(concat('%',#{realName}),'%')
and iut.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and iut.is_enabled = #{enabled}
and ( iut.target_login_name like concat(concat('%',#{params}),'%') or iut.real_name like concat(concat('%',#{params}),'%') )
) t1 order by t1.is_enabled desc, t1.id desc
limit #{start}, #{size}
) t2 left join iam_member_role imr2 on (imr2.member_id = t2.id and imr2.member_type = 'user' and imr2.source_id = #{sourceId} and imr2.source_type = #{sourceType}) left join iam_role ir2 on ir2.id = imr2.role_id left join fd_organization fo on (t2.organization_id = fo.id)

select t2.id,t2.real_name,t2.target_login_name as login_name,t2.is_enabled, t2.image_url, t2.is_locked,t2.organization_id,t2.email,t2.language,t2.time_zone, t2.phone, t2.object_version_number as user_version_number, fo.name as user_org_name,fo.code as user_org_code, ir2.id as role_id,ir2.code as role_code, ir2.name as role_name, ir2.is_enabled as role_is_enabled,ir2.organization_id as role_org_id from ( select distinct t1.* from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu
WHERE iu.ID IN
#{userId,jdbcType=BIGINT}
) iut left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') JOIN iam_role ir2 on ir2.id=imr.role_id
imr.source_id = #{projectId,jdbcType=BIGINT} and imr.source_type = 'project'
) t1 ) t2 left join iam_member_role imr2 on (imr2.member_id = t2.id and imr2.member_type = 'user' and imr2.source_id = #{projectId,jdbcType=BIGINT} and imr2.source_type = 'project') left join iam_role ir2 on ir2.id = imr2.role_id left join fd_organization fo on (t2.organization_id = fo.id)

select t2.id,t2.real_name,t2.target_login_name as login_name,t2.is_enabled, t2.image_url, t2.is_locked,t2.organization_id, t2.email,t2.language,t2.time_zone, t2.phone, t2.object_version_number as user_version_number, ir2.id as role_id,ir2.code as role_code, ir2.name as role_name,ir2.is_enabled as role_is_enabled,ir2.organization_id as role_org_id from ( select distinct t1.* from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu
WHERE iu.ID IN
#{userId,jdbcType=BIGINT}
) iut left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') left join iam_role ir on ir.id = imr.role_id where imr.source_id = #{organization_id} and imr.source_type = 'organization' ) t1 order by t1.is_enabled desc, t1.id desc ) t2 left join iam_member_role imr2 on (imr2.member_id = t2.id and imr2.member_type = 'user' and imr2.source_id = #{organization_id} and imr2.source_type = 'organization') left join iam_role ir2 on ir2.id = imr2.role_id

SELECT imr.source_id FROM iam_member_role imr LEFT JOIN iam_role ir ON ir.id = imr.role_id LEFT JOIN hiam_label_rel hlr on hlr.data_id = ir.id LEFT JOIN iam_label il ON il.id = hlr.label_id
imr.member_type = 'user' AND imr.source_type = 'organization' AND imr.member_id = #{userId} AND imr.member_id = 1 AND hlr.data_type = 'ROLE' AND il.name = 'TENANT_ADMIN' AND imr.source_id in
#{orgId}

SELECT iu.*
FROM iam_user iu
WHERE iu.login_name = #{loginName,jdbcType=VARCHAR}

SELECT COUNT(1)
FROM fd_project_user fpu
INNER JOIN iam_role ir ON fpu.MEMBER_ROLE_ID = ir.id
INNER JOIN iam_member_role imr on fpu.MEMBER_ROLE_ID = imr.id
left JOIN hiam_label_rel hlr on hlr.data_id = ir.id
Left JOIN iam_label il on il.id = hlr.label_id
WHERE imr.member_id = #{userId}
AND fpu.project_id = #{projectId}
AND hlr.data_type = 'ROLE'
AND il.name = #{labelName}
