SELECT * FROM iam_user WHERE id IN
#{id}
AND is_enabled = 1

SELECT * FROM iam_user WHERE login_name IN
#{loginName}
AND is_enabled = 1

SELECT * FROM iam_user WHERE email IN
#{email}
AND is_enabled = 1

SELECT * FROM iam_user WHERE 1=1

    AND login_name LIKE concat(concat('%',#{userSearchDTO.loginName}),'%')


    AND organization_id = #{userSearchDTO.organizationId}


    AND is_ldap = #{userSearchDTO.ldap}


    AND real_name LIKE concat(concat('%',#{userSearchDTO.realName}),'%')


    AND is_enabled = #{userSearchDTO.enabled}


    AND is_locked = #{userSearchDTO.locked}


    AND is_admin = #{userSearchDTO.admin}


    AND email LIKE concat(concat('%',#{userSearchDTO.email}),'%')


    AND language LIKE concat(concat('%',#{userSearchDTO.language}),'%')


    and (
    login_name LIKE concat(concat('%',#{param}),'%') OR
    email LIKE concat(concat('%',#{param}),'%') OR
    real_name LIKE concat(concat('%',#{param}),'%')
    )

order by id desc

SELECT iu.id, iu.target_login_name as login_name, iu.email, iu.real_name, iu.is_enabled, role.id AS role_id, role.name, role.code, role.is_enabled as role_is_enabled, role.is_built_in as role_is_built_in, imr.id, imr.LAST_UPDATE_DATE as duplicated FROM ( SELECT temp_user.*, case when temp_user.is_ldap=1 then temp_user.login_name else temp_user.email end as target_login_name FROM ( SELECT imr.MEMBER_ID as ID, MAX(imr.last_update_date) AS newest_date FROM ( select iu1.*, case when iu1.is_ldap=1 then iu1.login_name else iu1.email end as target_login_name from iam_user iu1 ) iut LEFT JOIN iam_member_role imr ON imr.member_id = iut.id LEFT JOIN iam_role ir ON ir.id = imr.role_id WHERE imr.member_type = 'user' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType}
AND iut.target_login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')
AND iut.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')
AND ir.name LIKE concat(concat('%',#{roleAssignmentSearchDTO.roleName}),'%')
and ( iut.target_login_name LIKE concat(concat('%',#{param}),'%') OR iut.real_name LIKE concat(concat('%',#{param}),'%') OR ir.name LIKE concat(concat('%',#{param}),'%') OR imr.member_type LIKE concat(concat('%',#{param}),'%') )
GROUP BY imr.MEMBER_ID ) temp LEFT JOIN iam_user temp_user ON temp.ID = temp_user.ID ORDER BY temp.newest_date DESC
limit #{start}, #{size}
) iu LEFT JOIN iam_member_role imr ON iu.id = imr.member_id LEFT JOIN iam_role role ON imr.role_id = role.id WHERE imr.member_type = 'user' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType} ORDER BY imr.LAST_UPDATE_DATE DESC

