SELECT count(1) FROM ( SELECT DISTINCT iut.id FROM ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut LEFT JOIN iam_member_role imr on imr.member_id = iut.id LEFT JOIN iam_role ir on ir.id = imr.role_id WHERE imr.member_type = 'user' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType}
AND iut.target_login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')
AND iut.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')
AND ir.name LIKE concat(concat('%',#{roleAssignmentSearchDTO.roleName}),'%')
AND ( iut.target_login_name LIKE concat(concat('%',#{param}),'%') OR iut.real_name LIKE concat(concat('%',#{param}),'%') OR ir.name LIKE concat(concat('%',#{param}),'%') OR imr.member_type LIKE concat(concat('%',#{param}),'%') )
) t

SELECT COUNT(1) FROM (
SELECT DISTINCT iu.id FROM iam_member_role imr
INNER JOIN iam_user iu ON imr.member_id = iu.id
WHERE imr.role_id = #{roleId}
AND imr.member_type = #{memberType}

    AND imr.source_id = #{sourceId}


    AND imr.source_type = #{sourceType}



        AND iu.login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')


        AND iu.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')



    AND (
    iu.login_name LIKE concat(concat('%',#{param}),'%') OR
    iu.real_name LIKE concat(concat('%',#{param}),'%')
    )

) t

SELECT iu.*,imr.source_id
FROM iam_user iu
INNER JOIN iam_member_role imr ON imr.member_id = iu.id

    imr.member_type = #{memberType} and imr.source_type = #{sourceType}
    AND imr.source_id = #{sourceId} and imr.role_id = #{roleId}


            AND iu.login_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.loginName}),'%')


            AND iu.real_name LIKE concat(concat('%',#{roleAssignmentSearchDTO.realName}),'%')


            AND iu.is_enabled = #{roleAssignmentSearchDTO.enabled}



        AND (
        iu.login_name LIKE concat(concat('%',#{param}),'%') OR
        iu.real_name LIKE concat(concat('%',#{param}),'%')
        )


order by imr.creation_date desc

select t2.id,t2.real_name,t2.target_login_name as login_name,t2.is_enabled, t2.is_locked,t2.organization_id,t2.email,t2.language,t2.time_zone, t2.phone, t2.object_version_number as user_version_number, fo2.name as user_org_name,fo2.code as user_org_code, ir2.id as role_id,ir2.code as role_code, ir2.name as role_name, ir2.is_enabled as role_is_enabled,ir2.organization_id as role_org_id from ( select distinct t1.* from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut
left join fd_organization fo on fo.id = iut.organization_id
left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') left join iam_role ir on ir.id = imr.role_id
and fo.name like concat(concat('%',#{orgName}),'%')
and imr.source_id = #{sourceId} and imr.source_type = #{sourceType} and ir.name like concat(concat('%',#{roleName}),'%')
and iut.real_name LIKE concat(concat('%',#{realName}),'%')
and iut.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and iut.is_enabled = #{enabled}
and iut.is_locked = #{locked}
and ( iut.target_login_name like concat(concat('%',#{params}),'%') or iut.real_name like concat(concat('%',#{params}),'%') )
) t1 order by t1.is_enabled desc, t1.id desc
limit #{start}, #{size}
) t2 left join iam_member_role imr2 on (imr2.member_id = t2.id and imr2.member_type = 'user' and imr2.source_id = #{sourceId} and imr2.source_type = #{sourceType}) left join iam_role ir2 on ir2.id = imr2.role_id left join fd_organization fo2 on (t2.organization_id = fo2.id)

select count(1) from ( select distinct t1.id from ( select iut.* from ( select iu.*, case when iu.is_ldap=1 then iu.login_name else iu.email end as target_login_name from iam_user iu ) iut left join iam_member_role imr on (imr.member_id = iut.id and imr.member_type = 'user') left join iam_role ir on ir.id = imr.role_id where imr.source_id = #{sourceId} and imr.source_type = #{sourceType}
and ir.name like concat(concat('%',#{roleName}),'%')
union select iu2.*, case when iu2.is_ldap=1 then iu2.login_name else iu2.email end as target_login_name from iam_user iu2 where iu2.organization_id = #{sourceId}
) t1
and t1.real_name LIKE concat(concat('%',#{realName}),'%')
and t1.target_login_name LIKE concat(concat('%',#{loginName}),'%')
and t1.is_enabled = #{enabled}
and t1.is_locked = #{locked}
and ( t1.target_login_name like concat(concat('%',#{params}),'%') or t1.real_name like concat(concat('%',#{params}),'%') )
) t

