mapper.delete:
DELETE FROM fd_work_group
WHERE organization_id = #{organizationId}
AND id IN

    #{workGroupId}

mapper.select:
select min(rank) from fd_work_group
where organization_id = #{organizationId}
and parent_id = #{parentId}

select max(rank) from fd_work_group
where organization_id = #{organizationId}
and parent_id = #{parentId}

select * from fd_work_group
where organization_id = #{organizationId}
order by rank

select rank from fd_work_group
where organization_id = #{organizationId}
and parent_id = #{parentId}
and id = #{outSetId}

select max(rank) from fd_work_group
where organization_id = #{organizationId}
and parent_id = #{parentId}
and rank < #{rightRank}

select min(rank) from fd_work_group
where organization_id = #{organizationId}
and parent_id = #{parentId}
and rank > #{leftRank}

select id
from fd_work_group
where organization_id = #{organizationId}

    and id not in

        #{workGroupId}

mapper.update:
update fd_work_group
set open_object_id = #{openObjectId},
open_type = #{openType}
where id = #{id}
