mapper.delete:
DELETE FROM fd_work_group_user_rel
WHERE organization_id = #{organizationId}
AND work_group_id IN

    #{workGroupId}

DELETE FROM fd_work_group_user_rel
WHERE organization_id = #{organizationId}
AND work_group_id in

    #{workGroupId}

AND user_id in

    #{userId}

mapper.select:
select user_id
from fd_work_group_user_rel
where organization_id = #{organizationId}

    and work_group_id = #{workGroupId}

select user_id
from fd_work_group_user_rel
where organization_id = #{organizationId}

    and work_group_id in

        #{workGroupId}

select fwg.name, fwg.id, fwg.parent_id, fwg.object_version_number, fwgur.user_id from fd_work_group_user_rel fwgur JOIN fd_work_group fwg ON fwg.id = fwgur.work_group_id where fwgur.organization_id = #{organizationId}
and user_id in
#{userId}
